import 'package:flutter/material.dart';

class Activity {
  final Image image;
  final String title;
  final String description;
  final String? note;
  final String? location;
  final String date;
  final String? routeName;

  Activity.withNetworkImage({
    required String imageUrl,
    required this.title,
    required this.description,
    this.note,
    this.location,
    required this.date,
    this.routeName,
  }) : image = Image.network(
          imageUrl,
          fit: BoxFit.fitWidth,
    errorBuilder: (context, error, stackTrace) => Image.asset(
      'images/macbook.png',
      fit: BoxFit.fitWidth,
    ),
        );

  Activity.withAssetImage({
    required String imageName,
    required this.title,
    required this.description,
    this.note,
    this.location,
    required this.date,
    this.routeName,
  }) : image = Image.asset(
          'images/$imageName',
          fit: BoxFit.fitWidth,
        );
}

